mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  birthday = #{birthday,jdbcType=DATE},
  sex = #{sex,jdbcType=CHAR},
  address = #{address,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, birthday, sex, address
from user
where id = #{id,jdbcType=INTEGER}

